import {React, useState} from 'react'

export const ItemCount = ({stock, onAdd}) => {
    const [cont, setCont] = useState(0);

    function sumar () {
      if (cont < stock) {
        setCont (cont + 1)
      }
    }

    function restar () {
        if (cont > stock) {
            setCont (cont -1)
        }
    }

    function limpiar () {
            setCont (1)
    }

  return (
    <>
    <p>Stock: {stock} </p>
    <p>Cantidad: {cont} </p>
  <button className='btn btn-primary' onClick={() => sumar ("+")}> + </button>
  <button className='btn btn-secondary' onClick={() => restar ("-")}> - </button>
  <button className='btn btn-dark' onClick={() => limpiar ("reset")}> limpiar </button>
  </>
  )
}
