import {React, useState} from 'react';
import "./CartWidget/CartWidget.css"
import {Navbar} from './Navbar/Navbar';
import {ItemListContainer} from './ItemListContainer.jsx/ItemListContainer';

export const App = () => {
  const [cont, setCont] = useState(0);

  function contador (operacion) {
    if (operacion == "+") {
      setCont (cont + 1)
    }
    else {
      setCont (cont - 1)
    }
  }
  return (
    <>
        <Navbar/>
        <ItemListContainer title="titulo" subtitle="subtitulo"/>
        <p>
          Contador: {cont}
        </p>
        <button className='btn btn-primary' onClick={() => contador ("+")}>
        +
        </button>
        <button className='btn btn-secondary' onClick={() => contador ("-")}>
        -
        </button>
    </>
  )
}
